import React, { Component } from 'react';
import axios from 'axios';
import './App.css';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.css';
import Title from './Title'
// import os from 'os';

class App extends Component {

   constructor(props) {
    super(props);

    this.state = {
      isLoading: false,
        selectedFile:null,
      result: ""
    };
    this.onFileUpload = this.onFileUpload.bind(this);
  }

  // on file select
  onFileChange = (event) => {
     this.setState({selectedFile: event.target.files[0]})
}

//on file upload
  onFileUpload(event) {
      event.preventDefault();
      let f = this.state.selectedFile;
      let f_name = this.state.selectedFile.name;
      console.log(f_name);

      let fileReader = new FileReader();
      fileReader.readAsDataURL(f);
      fileReader.onload = (event) => {

          let toPost = {
              'file_name': f_name,
              'file_data': event.target.result
          };
          console.log(toPost);
          return axios.post('http://127.0.0.1:5000/api/uploadfile', toPost)
              .then(res => console.log(res))
              .catch(err => console.warn(err));
      }

  }


  render() {
    const isLoading = this.state.isLoading;
    const result = this.state.result;

   return (
       <Container>
           <div><h1><Title /></h1></div>

         <div className="container">
         <div className="row">

           <div className="col-md-6 center">
             <Form method="post" action="#" id="#">


               <div className="form-group files color">

                 <label style={{color: "white"}}>
                     Upload Your File
                 </label>
                 <input type="file" className="form-control"
                 onChange={this.onFileChange}/>

                   <button type="button" className="btn btn-success btn-block" onClick={this.onFileUpload}>
                       Upload
                   </button>

               </div>

             </Form>


           </div>
         </div>
       </div>
       </Container>
      );
  }
}

export default App;


